-- Create vehicle_images table for image gallery
create table vehicle_images (
  id uuid primary key default gen_random_uuid(),
  vehicle_id uuid not null references vehicles(id) on delete cascade,
  image_url text not null,
  display_order integer not null default 0,
  created_at timestamptz not null default timezone('utc'::text, now())
);

-- Enable RLS
alter table vehicle_images enable row level security;

-- Create indexes
create index vehicle_images_vehicle_id_idx on vehicle_images using btree (vehicle_id);
create index vehicle_images_display_order_idx on vehicle_images using btree (display_order);

-- RLS Policies for vehicle_images
create policy "Users can view images of their own vehicles"
on vehicle_images
for select
to authenticated
using (
  vehicle_id in (
    select id from vehicles where user_id = (select auth.uid())
  )
);

create policy "Users can view images of public vehicles"
on vehicle_images
for select
to authenticated
using (
  vehicle_id in (
    select id from vehicles where is_public = true
  )
);

create policy "Users can insert images for their own vehicles"
on vehicle_images
for insert
to authenticated
with check (
  vehicle_id in (
    select id from vehicles where user_id = (select auth.uid())
  )
);

create policy "Users can update images of their own vehicles"
on vehicle_images
for update
to authenticated
using (
  vehicle_id in (
    select id from vehicles where user_id = (select auth.uid())
  )
)
with check (
  vehicle_id in (
    select id from vehicles where user_id = (select auth.uid())
  )
);

create policy "Users can delete images of their own vehicles"
on vehicle_images
for delete
to authenticated
using (
  vehicle_id in (
    select id from vehicles where user_id = (select auth.uid())
  )
);

-- Create vehicle_modifications table
create table vehicle_modifications (
  id uuid primary key default gen_random_uuid(),
  vehicle_id uuid not null references vehicles(id) on delete cascade,
  category text not null check (
    category in (
      'Engine',
      'Intake',
      'Exhaust',
      'Suspension',
      'Wheels & Tyres',
      'Brakes',
      'Aero',
      'Electronics',
      'Interior'
    )
  ),
  brand_name text,
  part_name text,
  description text,
  created_at timestamptz not null default timezone('utc'::text, now()),
  updated_at timestamptz not null default timezone('utc'::text, now())
);

-- Enable RLS
alter table vehicle_modifications enable row level security;

-- Create indexes
create index vehicle_modifications_vehicle_id_idx on vehicle_modifications using btree (vehicle_id);
create index vehicle_modifications_category_idx on vehicle_modifications using btree (category);

-- RLS Policies for vehicle_modifications
create policy "Users can view modifications of their own vehicles"
on vehicle_modifications
for select
to authenticated
using (
  vehicle_id in (
    select id from vehicles where user_id = (select auth.uid())
  )
);

create policy "Users can view modifications of public vehicles"
on vehicle_modifications
for select
to authenticated
using (
  vehicle_id in (
    select id from vehicles where is_public = true
  )
);

create policy "Users can insert modifications for their own vehicles"
on vehicle_modifications
for insert
to authenticated
with check (
  vehicle_id in (
    select id from vehicles where user_id = (select auth.uid())
  )
);

create policy "Users can update modifications of their own vehicles"
on vehicle_modifications
for update
to authenticated
using (
  vehicle_id in (
    select id from vehicles where user_id = (select auth.uid())
  )
)
with check (
  vehicle_id in (
    select id from vehicles where user_id = (select auth.uid())
  )
);

create policy "Users can delete modifications of their own vehicles"
on vehicle_modifications
for delete
to authenticated
using (
  vehicle_id in (
    select id from vehicles where user_id = (select auth.uid())
  )
);

-- Create vehicle_beacons table to store BLE beacon associations
create table vehicle_beacons (
  id uuid primary key default gen_random_uuid(),
  vehicle_id uuid not null references vehicles(id) on delete cascade,
  beacon_uuid text not null,
  beacon_major integer,
  beacon_minor integer,
  created_at timestamptz default timezone('utc'::text, now()),
  updated_at timestamptz default timezone('utc'::text, now()),
  unique(beacon_uuid, beacon_major, beacon_minor)
);

-- Enable RLS
alter table vehicle_beacons enable row level security;

-- RLS policies
create policy "Users can view beacons for public vehicles"
  on vehicle_beacons for select
  using (
    exists (
      select 1 from vehicles
      where vehicles.id = vehicle_beacons.vehicle_id
      and vehicles.is_public = true
    )
  );

create policy "Users can manage their own vehicle beacons"
  on vehicle_beacons for all
  using (
    exists (
      select 1 from vehicles
      where vehicles.id = vehicle_beacons.vehicle_id
      and vehicles.user_id = auth.uid()
    )
  );

-- Create index for faster lookups
create index vehicle_beacons_vehicle_id_idx on vehicle_beacons(vehicle_id);
create index vehicle_beacons_beacon_uuid_idx on vehicle_beacons(beacon_uuid);

-- Create event_checkins table
create table event_checkins (
  id uuid primary key default gen_random_uuid(),
  event_id uuid not null references events(id) on delete cascade,
  user_id uuid not null references auth.users(id) on delete cascade,
  vehicle_id uuid references vehicles(id) on delete set null,
  checked_in_at timestamptz not null default timezone('utc'::text, now()),
  unique(event_id, user_id)
);

-- Create indexes for performance
create index event_checkins_event_id_idx on event_checkins using btree (event_id);
create index event_checkins_user_id_idx on event_checkins using btree (user_id);
create index event_checkins_vehicle_id_idx on event_checkins using btree (vehicle_id);

-- Enable RLS
alter table event_checkins enable row level security;

-- RLS Policies for event_checkins
create policy "Event check-ins are viewable by club members"
  on event_checkins
  for select
  to authenticated
  using (
    event_id in (
      select id
      from events
      where club_id in (
        select club_id
        from club_members
        where user_id = (select auth.uid())
      )
    )
  );

create policy "Users can check in to events"
  on event_checkins
  for insert
  to authenticated
  with check (
    (select auth.uid()) = user_id
    and event_id in (
      select id
      from events
      where club_id in (
        select club_id
        from club_members
        where user_id = (select auth.uid())
      )
    )
    and (
      vehicle_id is null
      or vehicle_id in (
        select id
        from vehicles
        where user_id = (select auth.uid())
      )
    )
  );

create policy "Users can delete their own check-ins"
  on event_checkins
  for delete
  to authenticated
  using ((select auth.uid()) = user_id);

-- Add featured flag and location fields to vehicles for Discover feed
alter table vehicles
  add column is_featured boolean not null default false,
  add column latitude double precision,
  add column longitude double precision,
  add column location_updated_at timestamptz;

-- Create indexes for Discover feed queries
create index vehicles_is_featured_idx on vehicles using btree (is_featured);
create index vehicles_updated_at_idx on vehicles using btree (updated_at desc);
create index vehicles_location_idx on vehicles using btree (latitude, longitude) where latitude is not null and longitude is not null;

-- Add premium subscription tracking
CREATE TABLE IF NOT EXISTS user_subscriptions (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE NOT NULL,
  subscription_status TEXT NOT NULL DEFAULT 'free' CHECK (subscription_status IN ('free', 'premium')),
  subscription_start_date TIMESTAMPTZ,
  subscription_end_date TIMESTAMPTZ,
  created_at TIMESTAMPTZ DEFAULT timezone('utc'::text, now()) NOT NULL,
  updated_at TIMESTAMPTZ DEFAULT timezone('utc'::text, now()) NOT NULL,
  UNIQUE(user_id)
);

-- Add beacon detection history
CREATE TABLE IF NOT EXISTS beacon_detections (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  detector_user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE NOT NULL,
  detected_vehicle_id UUID REFERENCES vehicles(id) ON DELETE CASCADE NOT NULL,
  detected_user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE NOT NULL,
  rssi INTEGER NOT NULL,
  location TEXT,
  detected_at TIMESTAMPTZ DEFAULT timezone('utc'::text, now()) NOT NULL
);

-- Add vehicle timeline entries (diary)
CREATE TABLE IF NOT EXISTS vehicle_timeline (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  vehicle_id UUID REFERENCES vehicles(id) ON DELETE CASCADE NOT NULL,
  title TEXT NOT NULL CHECK (char_length(title) >= 1 AND char_length(title) <= 200),
  description TEXT,
  entry_date DATE NOT NULL,
  image_url TEXT,
  created_at TIMESTAMPTZ DEFAULT timezone('utc'::text, now()) NOT NULL,
  updated_at TIMESTAMPTZ DEFAULT timezone('utc'::text, now()) NOT NULL
);

-- Add vehicle performance data
CREATE TABLE IF NOT EXISTS vehicle_performance (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  vehicle_id UUID REFERENCES vehicles(id) ON DELETE CASCADE NOT NULL,
  zero_to_sixty DECIMAL(4, 2),
  quarter_mile_time DECIMAL(5, 2),
  quarter_mile_speed DECIMAL(5, 2),
  horsepower INTEGER,
  torque INTEGER,
  weight_lbs INTEGER,
  created_at TIMESTAMPTZ DEFAULT timezone('utc'::text, now()) NOT NULL,
  updated_at TIMESTAMPTZ DEFAULT timezone('utc'::text, now()) NOT NULL,
  UNIQUE(vehicle_id)
);

-- Update vehicle_modifications to add more fields
ALTER TABLE vehicle_modifications 
  ADD COLUMN IF NOT EXISTS install_date DATE,
  ADD COLUMN IF NOT EXISTS cost DECIMAL(10, 2),
  ADD COLUMN IF NOT EXISTS image_url TEXT;

-- Add club chat messages
CREATE TABLE IF NOT EXISTS club_messages (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  club_id UUID REFERENCES clubs(id) ON DELETE CASCADE NOT NULL,
  user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE NOT NULL,
  message TEXT NOT NULL CHECK (char_length(message) >= 1 AND char_length(message) <= 2000),
  created_at TIMESTAMPTZ DEFAULT timezone('utc'::text, now()) NOT NULL
);

-- Add club posts/feed
CREATE TABLE IF NOT EXISTS club_posts (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  club_id UUID REFERENCES clubs(id) ON DELETE CASCADE NOT NULL,
  user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE NOT NULL,
  content TEXT NOT NULL CHECK (char_length(content) >= 1 AND char_length(content) <= 5000),
  image_url TEXT,
  created_at TIMESTAMPTZ DEFAULT timezone('utc'::text, now()) NOT NULL,
  updated_at TIMESTAMPTZ DEFAULT timezone('utc'::text, now()) NOT NULL
);

-- Add club hubs (physical devices)
CREATE TABLE IF NOT EXISTS club_hubs (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  club_id UUID REFERENCES clubs(id) ON DELETE CASCADE NOT NULL,
  name TEXT NOT NULL CHECK (char_length(name) >= 3 AND char_length(name) <= 100),
  location TEXT NOT NULL,
  latitude DOUBLE PRECISION,
  longitude DOUBLE PRECISION,
  is_active BOOLEAN DEFAULT true NOT NULL,
  sync_interval_minutes INTEGER DEFAULT 15 NOT NULL,
  created_by UUID REFERENCES auth.users(id) ON DELETE SET NULL,
  created_at TIMESTAMPTZ DEFAULT timezone('utc'::text, now()) NOT NULL,
  updated_at TIMESTAMPTZ DEFAULT timezone('utc'::text, now()) NOT NULL
);

-- Add hub detection logs
CREATE TABLE IF NOT EXISTS hub_detections (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  hub_id UUID REFERENCES club_hubs(id) ON DELETE CASCADE NOT NULL,
  vehicle_id UUID REFERENCES vehicles(id) ON DELETE CASCADE NOT NULL,
  user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE NOT NULL,
  rssi INTEGER NOT NULL,
  detected_at TIMESTAMPTZ DEFAULT timezone('utc'::text, now()) NOT NULL
);

-- Add user follows
CREATE TABLE IF NOT EXISTS user_follows (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  follower_id UUID REFERENCES auth.users(id) ON DELETE CASCADE NOT NULL,
  following_id UUID REFERENCES auth.users(id) ON DELETE CASCADE NOT NULL,
  created_at TIMESTAMPTZ DEFAULT timezone('utc'::text, now()) NOT NULL,
  UNIQUE(follower_id, following_id),
  CHECK (follower_id != following_id)
);

-- Add profile cover photos and additional fields
ALTER TABLE profiles 
  ADD COLUMN IF NOT EXISTS cover_photo_url TEXT;

-- Add event beacon association
ALTER TABLE events
  ADD COLUMN IF NOT EXISTS beacon_uuid TEXT,
  ADD COLUMN IF NOT EXISTS auto_checkin_enabled BOOLEAN DEFAULT false NOT NULL;

-- Add club logo and banner
ALTER TABLE clubs
  ADD COLUMN IF NOT EXISTS logo_url TEXT,
  ADD COLUMN IF NOT EXISTS banner_url TEXT;

-- Enable RLS on all new tables
ALTER TABLE user_subscriptions ENABLE ROW LEVEL SECURITY;
ALTER TABLE beacon_detections ENABLE ROW LEVEL SECURITY;
ALTER TABLE vehicle_timeline ENABLE ROW LEVEL SECURITY;
ALTER TABLE vehicle_performance ENABLE ROW LEVEL SECURITY;
ALTER TABLE club_messages ENABLE ROW LEVEL SECURITY;
ALTER TABLE club_posts ENABLE ROW LEVEL SECURITY;
ALTER TABLE club_hubs ENABLE ROW LEVEL SECURITY;
ALTER TABLE hub_detections ENABLE ROW LEVEL SECURITY;
ALTER TABLE user_follows ENABLE ROW LEVEL SECURITY;

-- RLS Policies for user_subscriptions
CREATE POLICY "Users can view their own subscription" ON user_subscriptions
  FOR SELECT USING (auth.uid() = user_id);

CREATE POLICY "Users can update their own subscription" ON user_subscriptions
  FOR UPDATE USING (auth.uid() = user_id);

CREATE POLICY "Users can insert their own subscription" ON user_subscriptions
  FOR INSERT WITH CHECK (auth.uid() = user_id);

-- RLS Policies for beacon_detections
CREATE POLICY "Users can view their own detections" ON beacon_detections
  FOR SELECT USING (auth.uid() = detector_user_id);

CREATE POLICY "Users can insert their own detections" ON beacon_detections
  FOR INSERT WITH CHECK (auth.uid() = detector_user_id);

-- RLS Policies for vehicle_timeline
CREATE POLICY "Vehicle owners can manage timeline" ON vehicle_timeline
  FOR ALL USING (
    EXISTS (
      SELECT 1 FROM vehicles
      WHERE vehicles.id = vehicle_timeline.vehicle_id
      AND vehicles.user_id = auth.uid()
    )
  );

CREATE POLICY "Public vehicles timeline is viewable" ON vehicle_timeline
  FOR SELECT USING (
    EXISTS (
      SELECT 1 FROM vehicles
      WHERE vehicles.id = vehicle_timeline.vehicle_id
      AND vehicles.is_public = true
    )
  );

-- RLS Policies for vehicle_performance
CREATE POLICY "Vehicle owners can manage performance" ON vehicle_performance
  FOR ALL USING (
    EXISTS (
      SELECT 1 FROM vehicles
      WHERE vehicles.id = vehicle_performance.vehicle_id
      AND vehicles.user_id = auth.uid()
    )
  );

CREATE POLICY "Public vehicles performance is viewable" ON vehicle_performance
  FOR SELECT USING (
    EXISTS (
      SELECT 1 FROM vehicles
      WHERE vehicles.id = vehicle_performance.vehicle_id
      AND vehicles.is_public = true
    )
  );

-- RLS Policies for club_messages
CREATE POLICY "Club members can view messages" ON club_messages
  FOR SELECT USING (
    EXISTS (
      SELECT 1 FROM club_members
      WHERE club_members.club_id = club_messages.club_id
      AND club_members.user_id = auth.uid()
    )
  );

CREATE POLICY "Club members can send messages" ON club_messages
  FOR INSERT WITH CHECK (
    EXISTS (
      SELECT 1 FROM club_members
      WHERE club_members.club_id = club_messages.club_id
      AND club_members.user_id = auth.uid()
    )
    AND auth.uid() = user_id
  );

-- RLS Policies for club_posts
CREATE POLICY "Club members can view posts" ON club_posts
  FOR SELECT USING (
    EXISTS (
      SELECT 1 FROM club_members
      WHERE club_members.club_id = club_posts.club_id
      AND club_members.user_id = auth.uid()
    )
  );

CREATE POLICY "Club members can create posts" ON club_posts
  FOR INSERT WITH CHECK (
    EXISTS (
      SELECT 1 FROM club_members
      WHERE club_members.club_id = club_posts.club_id
      AND club_members.user_id = auth.uid()
    )
    AND auth.uid() = user_id
  );

CREATE POLICY "Post authors can update their posts" ON club_posts
  FOR UPDATE USING (auth.uid() = user_id);

CREATE POLICY "Post authors can delete their posts" ON club_posts
  FOR DELETE USING (auth.uid() = user_id);

-- RLS Policies for club_hubs
CREATE POLICY "Club members can view hubs" ON club_hubs
  FOR SELECT USING (
    EXISTS (
      SELECT 1 FROM club_members
      WHERE club_members.club_id = club_hubs.club_id
      AND club_members.user_id = auth.uid()
    )
  );

CREATE POLICY "Club admins can manage hubs" ON club_hubs
  FOR ALL USING (
    EXISTS (
      SELECT 1 FROM club_members
      WHERE club_members.club_id = club_hubs.club_id
      AND club_members.user_id = auth.uid()
      AND club_members.role IN ('owner', 'admin')
    )
  );

-- RLS Policies for hub_detections
CREATE POLICY "Club members can view hub detections" ON hub_detections
  FOR SELECT USING (
    EXISTS (
      SELECT 1 FROM club_hubs
      JOIN club_members ON club_members.club_id = club_hubs.club_id
      WHERE club_hubs.id = hub_detections.hub_id
      AND club_members.user_id = auth.uid()
    )
  );

CREATE POLICY "Hubs can insert detections" ON hub_detections
  FOR INSERT WITH CHECK (true);

-- RLS Policies for user_follows
CREATE POLICY "Users can view their follows" ON user_follows
  FOR SELECT USING (auth.uid() = follower_id OR auth.uid() = following_id);

CREATE POLICY "Users can manage their follows" ON user_follows
  FOR ALL USING (auth.uid() = follower_id);

-- Create indexes for performance
CREATE INDEX IF NOT EXISTS idx_beacon_detections_detector ON beacon_detections(detector_user_id, detected_at DESC);
CREATE INDEX IF NOT EXISTS idx_beacon_detections_detected ON beacon_detections(detected_vehicle_id, detected_at DESC);
CREATE INDEX IF NOT EXISTS idx_vehicle_timeline_vehicle ON vehicle_timeline(vehicle_id, entry_date DESC);
CREATE INDEX IF NOT EXISTS idx_club_messages_club ON club_messages(club_id, created_at DESC);
CREATE INDEX IF NOT EXISTS idx_club_posts_club ON club_posts(club_id, created_at DESC);
CREATE INDEX IF NOT EXISTS idx_hub_detections_hub ON hub_detections(hub_id, detected_at DESC);
CREATE INDEX IF NOT EXISTS idx_user_follows_follower ON user_follows(follower_id);
CREATE INDEX IF NOT EXISTS idx_user_follows_following ON user_follows(following_id);

-- Create function to broadcast club messages
CREATE OR REPLACE FUNCTION broadcast_club_message()
RETURNS TRIGGER AS $$
SECURITY DEFINER
LANGUAGE plpgsql
AS $$
BEGIN
  PERFORM realtime.broadcast_changes(
    'club:' || NEW.club_id::text || ':messages',
    TG_OP,
    TG_OP,
    TG_TABLE_NAME,
    TG_TABLE_SCHEMA,
    NEW,
    OLD
  );
  RETURN NEW;
END;
$$;

-- Create trigger for club messages
DROP TRIGGER IF EXISTS club_messages_broadcast_trigger ON club_messages;
CREATE TRIGGER club_messages_broadcast_trigger
  AFTER INSERT ON club_messages
  FOR EACH ROW EXECUTE FUNCTION broadcast_club_message();

-- Create function to broadcast event checkins
CREATE OR REPLACE FUNCTION broadcast_event_checkin()
RETURNS TRIGGER AS $$
SECURITY DEFINER
LANGUAGE plpgsql
AS $$
BEGIN
  PERFORM realtime.broadcast_changes(
    'event:' || NEW.event_id::text || ':checkins',
    TG_OP,
    TG_OP,
    TG_TABLE_NAME,
    TG_TABLE_SCHEMA,
    NEW,
    OLD
  );
  RETURN NEW;
END;
$$;

-- Create trigger for event checkins
DROP TRIGGER IF EXISTS event_checkins_broadcast_trigger ON event_checkins;
CREATE TRIGGER event_checkins_broadcast_trigger
  AFTER INSERT OR DELETE ON event_checkins
  FOR EACH ROW EXECUTE FUNCTION broadcast_event_checkin();

-- Create function to broadcast hub detections
CREATE OR REPLACE FUNCTION broadcast_hub_detection()
RETURNS TRIGGER AS $$
SECURITY DEFINER
LANGUAGE plpgsql
AS $$
BEGIN
  PERFORM realtime.broadcast_changes(
    'hub:' || NEW.hub_id::text || ':detections',
    TG_OP,
    TG_OP,
    TG_TABLE_NAME,
    TG_TABLE_SCHEMA,
    NEW,
    OLD
  );
  RETURN NEW;
END;
$$;

-- Create trigger for hub detections
DROP TRIGGER IF EXISTS hub_detections_broadcast_trigger ON hub_detections;
CREATE TRIGGER hub_detections_broadcast_trigger
  AFTER INSERT ON hub_detections
  FOR EACH ROW EXECUTE FUNCTION broadcast_hub_detection();

