-- Drop and recreate the trigger function with better error handling and logging
DROP FUNCTION IF EXISTS public.handle_new_user() CASCADE;

CREATE OR REPLACE FUNCTION public.handle_new_user()
RETURNS trigger
LANGUAGE plpgsql
SECURITY DEFINER
SET search_path TO 'public'
AS $$
DECLARE
  v_username text;
  v_display_name text;
BEGIN
  -- Extract username and display_name from metadata with fallbacks
  v_username := COALESCE(
    NEW.raw_user_meta_data->>'username',
    'user_' || substr(NEW.id::text, 1, 8)
  );

  v_display_name := COALESCE(
    NEW.raw_user_meta_data->>'display_name',
    'User'
  );

  -- Log the attempt (for debugging)
  RAISE LOG 'Creating profile for user % with username % and display_name %', 
    NEW.id, v_username, v_display_name;

  -- Insert the profile
  INSERT INTO public.profiles (
    id,
    username,
    display_name,
    bio,
    avatar_url,
    is_private,
    ghost_mode,
    free_premium,
    always_searching_enabled,
    attendance_mode,
    notification_preferences
  )
  VALUES (
    NEW.id,
    v_username,
    v_display_name,
    NULL,
    NULL,
    false,
    false,
    false,
    false,
    'manual',
    jsonb_build_object(
      'detection_type', 'vibration',
      'vibration', true,
      'sound', 'default'
    )
  );

  RAISE LOG 'Successfully created profile for user %', NEW.id;

  RETURN NEW;
EXCEPTION
  WHEN OTHERS THEN
    -- Log the error but don't fail the user creation
    RAISE LOG 'Error creating profile for user %: % %', NEW.id, SQLERRM, SQLSTATE;
    -- Re-raise the exception so we know something went wrong
    RAISE;
END;
$$;

-- Recreate the trigger
DROP TRIGGER IF EXISTS on_auth_user_created ON auth.users;

CREATE TRIGGER on_auth_user_created
  AFTER INSERT ON auth.users
  FOR EACH ROW
  EXECUTE FUNCTION public.handle_new_user();

-- Grant necessary permissions
GRANT USAGE ON SCHEMA public TO postgres, anon, authenticated, service_role;
GRANT ALL ON ALL TABLES IN SCHEMA public TO postgres, anon, authenticated, service_role;
GRANT ALL ON ALL SEQUENCES IN SCHEMA public TO postgres, anon, authenticated, service_role;
GRANT ALL ON ALL FUNCTIONS IN SCHEMA public TO postgres, anon, authenticated, service_role;

-- Ensure the trigger function can bypass RLS
ALTER FUNCTION public.handle_new_user() SECURITY DEFINER;

