-- Create function to automatically create club permissions when club is created
CREATE OR REPLACE FUNCTION create_club_permissions()
RETURNS TRIGGER AS $$
BEGIN
  INSERT INTO club_permissions (club_id)
  VALUES (NEW.id);
  RETURN NEW;
END;
$$ LANGUAGE plpgsql SECURITY DEFINER;

CREATE TRIGGER on_club_created
  AFTER INSERT ON clubs
  FOR EACH ROW
  EXECUTE FUNCTION create_club_permissions();

-- Create realtime broadcast trigger for new messages
CREATE OR REPLACE FUNCTION notify_new_message()
RETURNS TRIGGER AS $$
BEGIN
  PERFORM realtime.broadcast_changes(
    'conversation:' || NEW.conversation_id::text,
    TG_OP,
    TG_OP,
    TG_TABLE_NAME,
    TG_TABLE_SCHEMA,
    NEW,
    OLD
  );
  RETURN NEW;
END;
$$ LANGUAGE plpgsql SECURITY DEFINER;

CREATE TRIGGER on_private_message_created
  AFTER INSERT ON private_messages
  FOR EACH ROW
  EXECUTE FUNCTION notify_new_message();

-- Create realtime broadcast trigger for club messages
CREATE OR REPLACE FUNCTION notify_club_message()
RETURNS TRIGGER AS $$
BEGIN
  PERFORM realtime.broadcast_changes(
    'club:' || NEW.club_id::text || ':messages',
    TG_OP,
    TG_OP,
    TG_TABLE_NAME,
    TG_TABLE_SCHEMA,
    NEW,
    OLD
  );
  RETURN NEW;
END;
$$ LANGUAGE plpgsql SECURITY DEFINER;

CREATE TRIGGER on_club_message_created
  AFTER INSERT ON club_messages
  FOR EACH ROW
  EXECUTE FUNCTION notify_club_message();

-- Create realtime broadcast trigger for event gallery
CREATE OR REPLACE FUNCTION notify_gallery_upload()
RETURNS TRIGGER AS $$
BEGIN
  PERFORM realtime.broadcast_changes(
    'event:' || NEW.event_id::text || ':gallery',
    TG_OP,
    TG_OP,
    TG_TABLE_NAME,
    TG_TABLE_SCHEMA,
    NEW,
    OLD
  );
  RETURN NEW;
END;
$$ LANGUAGE plpgsql SECURITY DEFINER;

CREATE TRIGGER on_event_gallery_upload
  AFTER INSERT ON event_gallery
  FOR EACH ROW
  EXECUTE FUNCTION notify_gallery_upload();

