-- Create vehicle_images table for image gallery
create table vehicle_images (
  id uuid primary key default gen_random_uuid(),
  vehicle_id uuid not null references vehicles(id) on delete cascade,
  image_url text not null,
  display_order integer not null default 0,
  created_at timestamptz not null default timezone('utc'::text, now())
);

-- Enable RLS
alter table vehicle_images enable row level security;

-- Create indexes
create index vehicle_images_vehicle_id_idx on vehicle_images using btree (vehicle_id);
create index vehicle_images_display_order_idx on vehicle_images using btree (display_order);

-- RLS Policies for vehicle_images
create policy "Users can view images of their own vehicles"
on vehicle_images
for select
to authenticated
using (
  vehicle_id in (
    select id from vehicles where user_id = (select auth.uid())
  )
);

create policy "Users can view images of public vehicles"
on vehicle_images
for select
to authenticated
using (
  vehicle_id in (
    select id from vehicles where is_public = true
  )
);

create policy "Users can insert images for their own vehicles"
on vehicle_images
for insert
to authenticated
with check (
  vehicle_id in (
    select id from vehicles where user_id = (select auth.uid())
  )
);

create policy "Users can update images of their own vehicles"
on vehicle_images
for update
to authenticated
using (
  vehicle_id in (
    select id from vehicles where user_id = (select auth.uid())
  )
)
with check (
  vehicle_id in (
    select id from vehicles where user_id = (select auth.uid())
  )
);

create policy "Users can delete images of their own vehicles"
on vehicle_images
for delete
to authenticated
using (
  vehicle_id in (
    select id from vehicles where user_id = (select auth.uid())
  )
);

-- Create vehicle_modifications table
create table vehicle_modifications (
  id uuid primary key default gen_random_uuid(),
  vehicle_id uuid not null references vehicles(id) on delete cascade,
  category text not null check (
    category in (
      'Engine',
      'Intake',
      'Exhaust',
      'Suspension',
      'Wheels & Tyres',
      'Brakes',
      'Aero',
      'Electronics',
      'Interior'
    )
  ),
  brand_name text,
  part_name text,
  description text,
  created_at timestamptz not null default timezone('utc'::text, now()),
  updated_at timestamptz not null default timezone('utc'::text, now())
);

-- Enable RLS
alter table vehicle_modifications enable row level security;

-- Create indexes
create index vehicle_modifications_vehicle_id_idx on vehicle_modifications using btree (vehicle_id);
create index vehicle_modifications_category_idx on vehicle_modifications using btree (category);

-- RLS Policies for vehicle_modifications
create policy "Users can view modifications of their own vehicles"
on vehicle_modifications
for select
to authenticated
using (
  vehicle_id in (
    select id from vehicles where user_id = (select auth.uid())
  )
);

create policy "Users can view modifications of public vehicles"
on vehicle_modifications
for select
to authenticated
using (
  vehicle_id in (
    select id from vehicles where is_public = true
  )
);

create policy "Users can insert modifications for their own vehicles"
on vehicle_modifications
for insert
to authenticated
with check (
  vehicle_id in (
    select id from vehicles where user_id = (select auth.uid())
  )
);

create policy "Users can update modifications of their own vehicles"
on vehicle_modifications
for update
to authenticated
using (
  vehicle_id in (
    select id from vehicles where user_id = (select auth.uid())
  )
)
with check (
  vehicle_id in (
    select id from vehicles where user_id = (select auth.uid())
  )
);

create policy "Users can delete modifications of their own vehicles"
on vehicle_modifications
for delete
to authenticated
using (
  vehicle_id in (
    select id from vehicles where user_id = (select auth.uid())
  )
);

-- Create vehicle_beacons table to store BLE beacon associations
create table vehicle_beacons (
  id uuid primary key default gen_random_uuid(),
  vehicle_id uuid not null references vehicles(id) on delete cascade,
  beacon_uuid text not null,
  beacon_major integer,
  beacon_minor integer,
  created_at timestamptz default timezone('utc'::text, now()),
  updated_at timestamptz default timezone('utc'::text, now()),
  unique(beacon_uuid, beacon_major, beacon_minor)
);

-- Enable RLS
alter table vehicle_beacons enable row level security;

-- RLS policies
create policy "Users can view beacons for public vehicles"
  on vehicle_beacons for select
  using (
    exists (
      select 1 from vehicles
      where vehicles.id = vehicle_beacons.vehicle_id
      and vehicles.is_public = true
    )
  );

create policy "Users can manage their own vehicle beacons"
  on vehicle_beacons for all
  using (
    exists (
      select 1 from vehicles
      where vehicles.id = vehicle_beacons.vehicle_id
      and vehicles.user_id = auth.uid()
    )
  );

-- Create index for faster lookups
create index vehicle_beacons_vehicle_id_idx on vehicle_beacons(vehicle_id);
create index vehicle_beacons_beacon_uuid_idx on vehicle_beacons(beacon_uuid);

-- Create event_checkins table
create table event_checkins (
  id uuid primary key default gen_random_uuid(),
  event_id uuid not null references events(id) on delete cascade,
  user_id uuid not null references auth.users(id) on delete cascade,
  vehicle_id uuid references vehicles(id) on delete set null,
  checked_in_at timestamptz not null default timezone('utc'::text, now()),
  unique(event_id, user_id)
);

-- Create indexes for performance
create index event_checkins_event_id_idx on event_checkins using btree (event_id);
create index event_checkins_user_id_idx on event_checkins using btree (user_id);
create index event_checkins_vehicle_id_idx on event_checkins using btree (vehicle_id);

-- Enable RLS
alter table event_checkins enable row level security;

-- RLS Policies for event_checkins
create policy "Event check-ins are viewable by club members"
  on event_checkins
  for select
  to authenticated
  using (
    event_id in (
      select id
      from events
      where club_id in (
        select club_id
        from club_members
        where user_id = (select auth.uid())
      )
    )
  );

create policy "Users can check in to events"
  on event_checkins
  for insert
  to authenticated
  with check (
    (select auth.uid()) = user_id
    and event_id in (
      select id
      from events
      where club_id in (
        select club_id
        from club_members
        where user_id = (select auth.uid())
      )
    )
    and (
      vehicle_id is null
      or vehicle_id in (
        select id
        from vehicles
        where user_id = (select auth.uid())
      )
    )
  );

create policy "Users can delete their own check-ins"
  on event_checkins
  for delete
  to authenticated
  using ((select auth.uid()) = user_id);

-- Add featured flag and location fields to vehicles for Discover feed
alter table vehicles
  add column is_featured boolean not null default false,
  add column latitude double precision,
  add column longitude double precision,
  add column location_updated_at timestamptz;

-- Create indexes for Discover feed queries
create index vehicles_is_featured_idx on vehicles using btree (is_featured);
create index vehicles_updated_at_idx on vehicles using btree (updated_at desc);
create index vehicles_location_idx on vehicles using btree (latitude, longitude) where latitude is not null and longitude is not null;

