-- Add profile cover photos and additional fields
ALTER TABLE profiles 
  ADD COLUMN IF NOT EXISTS cover_photo_url TEXT;

-- Add event beacon association
ALTER TABLE events
  ADD COLUMN IF NOT EXISTS beacon_uuid TEXT,
  ADD COLUMN IF NOT EXISTS auto_checkin_enabled BOOLEAN DEFAULT false NOT NULL;

-- Add club logo and banner
ALTER TABLE clubs
  ADD COLUMN IF NOT EXISTS logo_url TEXT,
  ADD COLUMN IF NOT EXISTS banner_url TEXT;

-- Enable RLS on all new tables
