ALTER TABLE beacon_detections ENABLE ROW LEVEL SECURITY;
ALTER TABLE vehicle_timeline ENABLE ROW LEVEL SECURITY;
ALTER TABLE vehicle_performance ENABLE ROW LEVEL SECURITY;
ALTER TABLE club_messages ENABLE ROW LEVEL SECURITY;
ALTER TABLE club_posts ENABLE ROW LEVEL SECURITY;
ALTER TABLE club_hubs ENABLE ROW LEVEL SECURITY;
ALTER TABLE hub_detections ENABLE ROW LEVEL SECURITY;
ALTER TABLE user_follows ENABLE ROW LEVEL SECURITY;

-- RLS Policies for user_subscriptions
CREATE POLICY "Users can view their own subscription" ON user_subscriptions
  FOR SELECT USING (auth.uid() = user_id);

CREATE POLICY "Users can update their own subscription" ON user_subscriptions
  FOR UPDATE USING (auth.uid() = user_id);

CREATE POLICY "Users can insert their own subscription" ON user_subscriptions
  FOR INSERT WITH CHECK (auth.uid() = user_id);

-- RLS Policies for beacon_detections
CREATE POLICY "Users can view their own detections" ON beacon_detections
  FOR SELECT USING (auth.uid() = detector_user_id);

CREATE POLICY "Users can insert their own detections" ON beacon_detections
  FOR INSERT WITH CHECK (auth.uid() = detector_user_id);

-- RLS Policies for vehicle_timeline
CREATE POLICY "Vehicle owners can manage timeline" ON vehicle_timeline
  FOR ALL USING (
    EXISTS (
      SELECT 1 FROM vehicles
      WHERE vehicles.id = vehicle_timeline.vehicle_id
      AND vehicles.user_id = auth.uid()
    )
  );

CREATE POLICY "Public vehicles timeline is viewable" ON vehicle_timeline
  FOR SELECT USING (
    EXISTS (
      SELECT 1 FROM vehicles
      WHERE vehicles.id = vehicle_timeline.vehicle_id
      AND vehicles.is_public = true
    )
  );

-- RLS Policies for vehicle_performance
CREATE POLICY "Vehicle owners can manage performance" ON vehicle_performance
  FOR ALL USING (
    EXISTS (
