CREATE POLICY "Club members can view messages" ON club_messages
  FOR SELECT USING (
    EXISTS (
      SELECT 1 FROM club_members
      WHERE club_members.club_id = club_messages.club_id
      AND club_members.user_id = auth.uid()
    )
  );

CREATE POLICY "Club members can send messages" ON club_messages
  FOR INSERT WITH CHECK (
    EXISTS (
      SELECT 1 FROM club_members
      WHERE club_members.club_id = club_messages.club_id
      AND club_members.user_id = auth.uid()
    )
    AND auth.uid() = user_id
  );

-- RLS Policies for club_posts
CREATE POLICY "Club members can view posts" ON club_posts
  FOR SELECT USING (
    EXISTS (
      SELECT 1 FROM club_members
      WHERE club_members.club_id = club_posts.club_id
      AND club_members.user_id = auth.uid()
    )
  );

CREATE POLICY "Club members can create posts" ON club_posts
  FOR INSERT WITH CHECK (
    EXISTS (
      SELECT 1 FROM club_members
      WHERE club_members.club_id = club_posts.club_id
      AND club_members.user_id = auth.uid()
    )
    AND auth.uid() = user_id
  );

CREATE POLICY "Post authors can update their posts" ON club_posts
  FOR UPDATE USING (auth.uid() = user_id);

CREATE POLICY "Post authors can delete their posts" ON club_posts
  FOR DELETE USING (auth.uid() = user_id);

-- RLS Policies for club_hubs
CREATE POLICY "Club members can view hubs" ON club_hubs
  FOR SELECT USING (
    EXISTS (
      SELECT 1 FROM club_members
      WHERE club_members.club_id = club_hubs.club_id
      AND club_members.user_id = auth.uid()
    )
  );

CREATE POLICY "Club admins can manage hubs" ON club_hubs
  FOR ALL USING (
    EXISTS (
      SELECT 1 FROM club_members
      WHERE club_members.club_id = club_hubs.club_id
      AND club_members.user_id = auth.uid()
      AND club_members.role IN ('owner', 'admin')
    )
  );

-- RLS Policies for hub_detections
CREATE POLICY "Club members can view hub detections" ON hub_detections
  FOR SELECT USING (
    EXISTS (
      SELECT 1 FROM club_hubs
      JOIN club_members ON club_members.club_id = club_hubs.club_id
      WHERE club_hubs.id = hub_detections.hub_id
      AND club_members.user_id = auth.uid()
    )
  );

CREATE POLICY "Hubs can insert detections" ON hub_detections
  FOR INSERT WITH CHECK (true);

-- RLS Policies for user_follows
CREATE POLICY "Users can view their follows" ON user_follows
  FOR SELECT USING (auth.uid() = follower_id OR auth.uid() = following_id);

CREATE POLICY "Users can manage their follows" ON user_follows
  FOR ALL USING (auth.uid() = follower_id);

