CREATE INDEX IF NOT EXISTS idx_beacon_detections_detector ON beacon_detections(detector_user_id, detected_at DESC);
CREATE INDEX IF NOT EXISTS idx_beacon_detections_detected ON beacon_detections(detected_vehicle_id, detected_at DESC);
CREATE INDEX IF NOT EXISTS idx_vehicle_timeline_vehicle ON vehicle_timeline(vehicle_id, entry_date DESC);
CREATE INDEX IF NOT EXISTS idx_club_messages_club ON club_messages(club_id, created_at DESC);
CREATE INDEX IF NOT EXISTS idx_club_posts_club ON club_posts(club_id, created_at DESC);
CREATE INDEX IF NOT EXISTS idx_hub_detections_hub ON hub_detections(hub_id, detected_at DESC);
CREATE INDEX IF NOT EXISTS idx_user_follows_follower ON user_follows(follower_id);
