-- Create function to broadcast club messages
CREATE OR REPLACE FUNCTION broadcast_club_message()
RETURNS TRIGGER AS $$
SECURITY DEFINER
LANGUAGE plpgsql
AS $$
BEGIN
  PERFORM realtime.broadcast_changes(
    'club:' || NEW.club_id::text || ':messages',
    TG_OP,
    TG_OP,
    TG_TABLE_NAME,
    TG_TABLE_SCHEMA,
    NEW,
    OLD
  );
  RETURN NEW;
END;
$$;

-- Create trigger for club messages
DROP TRIGGER IF EXISTS club_messages_broadcast_trigger ON club_messages;
CREATE TRIGGER club_messages_broadcast_trigger
  AFTER INSERT ON club_messages
  FOR EACH ROW EXECUTE FUNCTION broadcast_club_message();

