    'event:' || NEW.event_id::text || ':checkins',
    TG_OP,
    TG_OP,
    TG_TABLE_NAME,
    TG_TABLE_SCHEMA,
    NEW,
    OLD
  );
  RETURN NEW;
END;
$$;

-- Create trigger for event checkins
DROP TRIGGER IF EXISTS event_checkins_broadcast_trigger ON event_checkins;
CREATE TRIGGER event_checkins_broadcast_trigger
  AFTER INSERT OR DELETE ON event_checkins
  FOR EACH ROW EXECUTE FUNCTION broadcast_event_checkin();

